import React, { useState, useEffect } from "react"
import axios from "axios";

import Tweet from "./Tweet.js";
import NewTweet from "./NewTweet.js";
import TweetDeleted from "./TweetDeleted.js";

import "../../stylesheets/components/timeline.css";

//props an array of tweets to render
//default: boolean
export default function Timeline(props){
    useEffect( () => {
        let listTmp = props.tweets.map( item => {
            return [item, false]; //[value, deleted?]
        })

        function genTweetsToRender(listToMap){
            console.log('tweetList: ', listToMap);
            return (
                listToMap.map(async (item) => {
                    const tweetId = item[0]['index']; 
                    if (!item[1])
                    {
                        return(
                            <Tweet 
                                key={tweetId}
                                id={tweetId}
                                tweet={item[0]}
                                default={props.default}
                                user={props.user}
                                deleted={false}
                                onDelete={handleHideTweet}
                            />
                        );
                    }
                    else
                    {
                        return(
                            <TweetDeleted 
                                key={tweetId} 
                                id={tweetId} 
                                deleted={true} 
                                onDelete={handleHideTweet}
                            />
                        );
                    }
                })
            )
        }

        setTweetList(listTmp);
        setTweets(genTweetsToRender(listTmp));

    }, [props.tweets]);

    const [tweetList, setTweetList] = useState([]);

    const [tweets, setTweets] = useState([]);

    function genTweetsToRender(listToMap){
        console.log('tweetList: ', listToMap);
        return (
            listToMap.map(async (item) => {
                const tweetId = item[0]['index']; 
                if (!item[1])
                {
                    return(
                        <Tweet 
                            key={tweetId}
                            id={tweetId}
                            tweet={item[0]}
                            default={props.default}
                            user={props.user}
                            deleted={false}
                            onDelete={handleHideTweet}
                        />
                    );
                }
                else
                {
                    return(
                        <TweetDeleted 
                            key={tweetId} 
                            id={tweetId} 
                            deleted={true} 
                            onDelete={handleHideTweet}
                        />
                    );
                }
            })
        )
    }

    function handleHideTweet(event, id){
        //console.log("handleHideTweet");
        setTweetList( prev => {
            //console.log("setTweetList");
            return( prev.map(item => {
                if(item[0]['index']===id)
                {
                    //console.log("hidden");
                    return [item[0], !item[1]];
                }
                else
                {
                    //console.log(id);
                    return item;
                }
            }));
        });
    }

    async function handleRenderNewTweet(event, newTweetId){
        await axios
            .get(`/apiTweet/tweet/${newTweetId}`)
            .then( (res) => {
                console.log('res.data: ', res.data);
                //console.log("handleRenderNewTweet");
                setTweetList( prev => {
                    //console.log("setter");
                    prev.unshift([res.data, false]);
                    return([...prev]);
                });
                setTweets(genTweetsToRender(tweetList));
            });


    }

    return (
        <div className="timeline column">
            <header className="timeline-header">
                <div onClick={props.scroll} className="timeline-home">Home</div>
            </header>
            {/* if default timeline, no new tweets needed */}
            {props.default || <NewTweet onPost={handleRenderNewTweet} user={props.user} />}
            <main id="timeline-main" className="timeline-main">
                <section className="timeline-tweets">
                    {tweets ? "" : tweets}
                </section>
            </main>
        </div>
    )
}
