import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import * as ROUTES from "../constants/routes.js";

import "../stylesheets/login.css";

export default function Login(){
    const initialFormState = {
        emailAddress: "",
        password: ""
    }

    const [form, setForm] = useState(initialFormState);

    //const [emailAddress, setEmailAddress] = useState("");
    //const [password, setPassword] = useState("");

    const [error, setError] = useState("");
    const isInvalid = form.password === "" || form.emailAddress === "";

    useEffect( () => {
        document.title = "Login - Birdy";
    }, []);

    const handleChange = (property, event) => {
        setForm( prev => ({...prev, property: event.target.value }))
    };

    return(
        <main className="login-main column gap20">
            <div className="login-form-container column gap20">
                <h1 className="birdy-logo-h1">Birdy</h1>
                <form className="login-form column" method="POST">
                    <input
                        className="login-form-input" 
                        type="email" 
                        placeholder="Email address" 
                        aria-label="Enter your email address"
                        onChange={handleChange}
                        value={form.emailAddress}
                    />
                    <input
                        className="login-form-input" 
                        type="password" 
                        placeholder="Password" 
                        aria-label="Enter your password"
                        onChange={handleChange}
                        value={form.password}                        
                    />
                    <input 
                        className={`login-form-submit ${isInvalid && "login-invalid"}`}
                        type="submit" 
                        value="Log In"
                        disabled={isInvalid}
                    />
                </form>
            </div>
            <div className="login-no-account">
                <p>
                    Don't have an account?&ensp;
                    <Link className="login-ref-signup" to={ROUTES.SIGN_UP}>
                        Sign up
                    </Link>
                </p>
            </div>
        </main>
    )
};
