import React, { useState } from "react"

import Tweet from "./Tweet.js";
import NewTweet from "./NewTweet.js";
import TweetDeleted from "./TweetDeleted.js";

import "../../stylesheets/components/timeline.css";

//props an array of tweets to render
//default: boolean
export default function Timeline(props){

    const tweets = props.tweets.map(item => {
        return (
            <Tweet 
                key={item.getId()}
                tweet={item}
                default={props.default}
                deleted={false}
                onDelete={handleDeleteTweet}
            />
        )
    })

    const [tweetList, setTweetList] = useState(tweets);

    function handleDeleteTweet(event){
        console.log("handleDeleteTweet");
        setTweetList( prev => {
            console.log("setTweetList");
            return( prev.map(item => {
                if(item.deleted)
                {
                    console.log("deleted");
                    return <TweetDeleted deleted={true} />
                }
                else
                {
                    console.log("not deleted");
                    return item;
                }
            }));
        });
    }

    return (
        <div className="timeline column">
            <header className="timeline-header">
                <div className="max-opacity">Home</div>
            </header>
            <main className="timeline-main">
                {/* if default timeline, no new tweets needed */}
                {props.default || <NewTweet />}
                <section>
                    {tweetList}
                </section>
            </main>
        </div>
    )
}
